const directory = "C:/Users/loicv/.node-red/data";

fs.readdir(directory, (err, files) => {
    if (err) {
        node.error('Unable to scan directory: ' + err);
        return;
    }

    msg.options = [];

    files.forEach(filename => {
        if (filename.endsWith(".tsv")) {
            msg.options.push({
                value: path.join(directory, filename),
                label: filename
            });
        }
    });

    node.send(msg);
});
