format:
/* Global CSS - All pages */

i.mdi-menu.mdi.v-icon.notranslate.v-theme--nrdb.v-icon--size-default {
    color: #0a6eca;
}

.v-navigation-drawer {
    border: 0px solid transparent;
    /* color: #000000;
    background-color: transparent !important; */
    color: #0a6eca;
    background-color: white !important;
    /* background-color: #0a6dca !important; */
}

.elevation-1 {
    box-shadow: 0 0px 0px 0px !important;
}

.v-toolbar {
    background-color: #ffffff !important;
    /* background-color: #0a6dca !important; */
}

.v-toolbar-title__placeholder {
    /* color: #000000 !important; */
    color: #0a6eca !important;
    font-weight: bold;
}

template {
    background-color: white !important;
}

/* Custom CSS - Home page */

.section-1 {
    padding: 0rem 20rem;
}

.section-2 {
    padding: 0rem 16rem;
}

.section-3 {
    padding: 0rem 16rem;
}

.section-4 {
    padding: 0rem 28rem;
}


/* .styled-button-visual */

.styled-button-visual .v-btn .v-icon {
    height: 0px !important;
    --v-icon-size-multiplier: 3;
    color: #0a6eca;
}

.styled-button-visual .v-btn {
    background-color:rgba(255, 0, 0, 0);
    padding: 2.5em 2.5em;
}

.styled-button .v-btn {
    background-color: #076eac;
}


/* .button-label */

.button-label span.nrdb-ui-text-value {
    color: #17426a;
    font-weight: bold;
    font-size: 14px;
    margin-bottom: 10%;
}

/* .styled-button */

.styled-button .v-btn .v-icon {
    height: 0px !important;
    --v-icon-size-multiplier: 3;
}

.styled-button .v-btn {
    padding: 3.5em 3.5em;
}

/* .title-top */

.title-top {
    margin-bottom: 25px;
}

/* .top-button */

.top-button {
    justify-content: center;
    width: 100%;
}

.top-button .v-btn {
    border-radius: 10px;
    background-color: #17426a;
}

.top-button .v-btn * {
    font-size: xx-large;
    padding: 0.5em 2em;
}

/* .information-text */

.information-text label {
    text-align: center;
    background-color: #ffffff;
    padding: 1% 12%;
    min-width: 30%;
    border-color: #0a6eca !important;
    /* border-radius: 10px; */
    border-bottom: 3px solid #0a6eca;
    color: #0a6eca;
    font-weight: bold;
    margin-bottom: 0.5rem;
}

.information-text span.nrdb-ui-text-value {
    color: #000000;
    font-size: 14px;
    margin-bottom: 11%;
}

/* .top-home */

.top-home {
    margin-bottom: 2rem;
    background-color: transparent !important;
}

