function parseLine(line) {
    const result = [];
    let currentItem = '';
    let inBraces = 0;

    for (let i = 0; i < line.length; i++) {
        const char = line[i];

        if (char === '{') {
            inBraces++;
        } else if (char === '}') {
            inBraces--;
        } else if (char === ',' && inBraces === 0) {
            result.push(currentItem);
            currentItem = '';
            continue;
        }

        currentItem += char;
    }

    result.push(currentItem); // Push the last item
    return result;
}

